function graficar(X1, Y1, Z1, S1, SolRatio1)
%CREATEFIGURE(X1, Y1, Z1, S1, SolRatio1)
%  X1:  scatter3 x
%  Y1:  scatter3 y
%  Z1:  scatter3 z
%  S1:  scatter3 s
%  SOLRATIO1:  scatter3 c

%  Auto-generated by MATLAB on 08-Jan-2019 14:42:44

% Create figure
figure1 = figure('Name',...
    'Datos de radiación solar en el valle de sacramento');

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create scatter3
scatter3(X1,Y1,Z1,S1,SolRatio1);

% Create zlabel
zlabel('Radiación solar');

% Create ylabel
ylabel('Año');

% Create xlabel
xlabel('Dia del año');

