% Uncomment the following line to preserve the Z-limits of the axes
% zlim(axes1,[-0.0313106608811002 0.9686893391189]);
view(axes1,[-37.5 13.9999999999999]);
grid(axes1,'on');
% Create colorbar
colorbar('peer',axes1);

